mapper.select:
select *,t2.sentence from corpus as t1 left join (select * from sentence) as t2  on t2.id=t1.sentence_id
where t1.del=0 and t2.del=0

    and t2.sentence like CONCAT('%',#{sentence},'%')



    and recorder_name = #{recorderName}


    and dialect = #{dialect}


    and sex = #{sex}


    and status = #{status}

mapper.delete:
update corpus
set del = 1
WHERE id = #{id}
