mapper.insert:
INSERT INTO
user (
         username,    password,  realname,                  phone,               address,             dept,                  level,sex
)
VALUES (
    #{userName}, #{passWord}, #{realName}, #{phone}, #{address}, #{dept}, #{level}, #{sex}
)

mapper.update:
UPDATE user
username = #{userName} , password = #{passWord} , realname = #{realName} , phone = #{phone} , address = #{address} , dept = #{dept} , level = #{level} , sex = #{sex} ,
where id = #{id}

mapper.delete:
update user
set is_delete = 1
WHERE id = #{id}
